class Amplifier implements MusicPlayer {
    private boolean isPoweredOn = false;
    private int volume = 30;
    private String equalizerSetting = "Flat";
    private boolean bassBoost = false;

    @Override
    public void play() {
        throw new UnsupportedOperationException("Amplifiers don't play music - they amplify audio signals!");
    }

    @Override
    public void pause() {
        throw new UnsupportedOperationException("Amplifiers don't control playback!");
    }

    @Override
    public void stop() {
        isPoweredOn = false;
        System.out.println("Amplifier: Powered OFF");
    }

    @Override
    public void skipTrack() {
        throw new UnsupportedOperationException("Amplifiers don't control track selection!");
    }

    @Override
    public void eject() {
        throw new UnsupportedOperationException("Amplifiers don't have media to eject!");
    }

    @Override
    public void setVolume(int volume) {
        if (volume < 0) {
            this.volume = 0;
        } else if (volume > 100) {
            this.volume = 100;
        } else {
            this.volume = volume;
        }
        if (this.volume > 80) {
            System.out.println("Amplifier: Warning - High volume!");
        }
        System.out.println("Amplifier: Master volume set to " + this.volume);
    }

    @Override
    public void setEqualizer(String preset) {
        switch (preset.toLowerCase()) {
            case "rock":
            case "pop":
            case "jazz":
            case "classical":
                equalizerSetting = preset;
                break;
            default:
                equalizerSetting = "Flat";
                System.out.println("Amplifier: Unknown preset. Defaulting to Flat.");
                return;
        }
        System.out.println("Amplifier: Equalizer set to " + equalizerSetting);
    }

    @Override
    public void displayTrackInfo() {
        throw new UnsupportedOperationException("Amplifiers don't track music information!");
    }

    @Override
    public void shuffle() {
        throw new UnsupportedOperationException("Amplifiers don't control playback modes!");
    }

    @Override
    public void repeat() {
        throw new UnsupportedOperationException("Amplifiers don't control playback modes!");
    }

    public void powerOn() {
        isPoweredOn = true;
        System.out.println("Amplifier: Powered ON");
    }

    public void setBassBoost(boolean enabled) {
        this.bassBoost = enabled;
        System.out.println("Amplifier: Bass boost " + (enabled ? "ON" : "OFF"));
    }
}

