public class Employee {
    private String name;
    private double baseSalary;
    private int yearsOfService;
    private String department;
    private String position;
    private String employeeId;
    private java.util.Date hireDate;
    private java.util.Date lastReviewDate;
    private double performanceRating;
    private int vacationDays;
    private int usedVacationDays;
    private String manager;
    private String email;

    private HRSystem hrSystem;
    private NotificationService notificationService;

    public Employee(String name, double baseSalary, int yearsOfService) {
        this.name = name;
        this.baseSalary = baseSalary;
        this.yearsOfService = yearsOfService;
        this.employeeId = generateEmployeeId();
        this.hireDate = new java.util.Date();
        this.vacationDays = 20;
        this.usedVacationDays = 0;
        this.performanceRating = 3.0;
        this.hrSystem = new HRSystem("https://hr.company.com");
        this.notificationService = new NotificationService("smtp.company.com");
    }

    public double calculateSalary() {
        return baseSalary + (yearsOfService * 1000);
    }

    public void printPaySlip() {
        System.out.println("Pay slip for " + name + ": $" + calculateSalary());
    }

    public double calculateBonus() {
        double baseBonus = baseSalary * 0.1;
        if (performanceRating >= 4.0) {
            baseBonus *= 1.5;
        } else if (performanceRating >= 3.5) {
            baseBonus *= 1.2;
        }
        return baseBonus;
    }

    public double calculateAnnualSalary() {
        return calculateSalary() * 12 + calculateBonus();
    }

    public void processPayroll() {
        double monthlySalary = calculateSalary();
        double taxes = calculateTaxes(monthlySalary);
        double netPay = monthlySalary - taxes;

        System.out.println("Processing payroll for " + name);
        System.out.println("Gross Pay: $" + monthlySalary);
        System.out.println("Taxes: $" + taxes);
        System.out.println("Net Pay: $" + netPay);

        hrSystem.updateEmployeeRecord(employeeId, "Payroll processed: $" + netPay);
        sendPayrollEmail();
    }

    public double calculateTaxes(double salary) {
        if (salary <= 3000) {
            return salary * 0.15;
        } else if (salary <= 6000) {
            return salary * 0.20;
        } else {
            return salary * 0.25;
        }
    }

    public boolean requestVacation(int days) {
        if (usedVacationDays + days <= vacationDays) {
            usedVacationDays += days;
            System.out.println("Vacation request approved for " + name + ": " + days + " days");
            hrSystem.updateEmployeeRecord(employeeId, "Vacation approved: " + days + " days");
            notifyManager("Vacation request approved for " + name);
            return true;
        } else {
            System.out.println("Vacation request denied for " + name + ": insufficient days");
            return false;
        }
    }

    public void notifyManager(String message) {
        if (manager != null) {
            notificationService.notifyManager(manager, message);
        }
    }

    public void sendPayrollEmail() {
        if (email != null) {
            notificationService.sendEmail(email, "Your monthly payslip", "Please find your payslip attached.");
        }
    }

    public void conductPerformanceReview() {
        System.out.println("Conducting performance review for " + name);
        System.out.println("Current rating: " + performanceRating);
        lastReviewDate = new java.util.Date();

        hrSystem.logPerformanceReview(employeeId, performanceRating);

        if (performanceRating >= 4.0) {
            System.out.println("Excellent performance! Recommending for promotion.");
        } else if (performanceRating < 2.0) {
            System.out.println("Performance improvement needed.");
        }
    }

    public void updatePerformanceRating(double rating) {
        if (rating >= 1.0 && rating <= 5.0) {
            this.performanceRating = rating;
            System.out.println("Performance rating updated to " + rating + " for " + name);
            hrSystem.updateEmployeeRecord(employeeId, "Performance rating: " + rating);
        } else {
            System.out.println("Invalid rating. Must be between 1.0 and 5.0");
        }
    }

    public String generateEmployeeId() {
        return "EMP" + System.currentTimeMillis() % 10000;
    }

    public void printEmployeeReport() {
        System.out.println("Employee Report for " + name);
        System.out.println("ID: " + employeeId);
        System.out.println("Department: " + department);
        System.out.println("Position: " + position);
        System.out.println("Hire Date: " + hireDate);
        System.out.println("Years of Service: " + yearsOfService);
        System.out.println("Base Salary: $" + baseSalary);
        System.out.println("Performance Rating: " + performanceRating);
        System.out.println("Vacation Days Remaining: " + (vacationDays - usedVacationDays));
