public class Book {
    private String title;
    private String author;
    private String content;
    private String isbn;
    private java.util.Date publishDate;
    private String genre;
    private int pageCount;
    private double price;
    private String publisher;
    private java.util.List<String> reviews;
    private double averageRating;
    private int totalSales;

    private FileStorage fileStorage;

    public Book(String title, String author, String content) {
        this.title = title;
        this.author = author;
        this.content = content;
        this.publishDate = new java.util.Date();
        this.reviews = new java.util.ArrayList<>();
        this.averageRating = 0.0;
        this.totalSales = 0;
        this.fileStorage = new FileStorage("/books/storage");

        generateISBN();
        calculatePageCount();
    }

    public void printBook() {
        System.out.println("Title: " + title);
        System.out.println("Author: " + author);
        System.out.println("Content: " + content);
    }

    public void saveToFile(String filename) {
        String bookData = formatBookData();
        fileStorage.saveToFile(filename, bookData);
    }

    public String formatBookData() {
        return "TITLE: " + title + "\nAUTHOR: " + author + "\nISBN: " + isbn + "\nPAGES: " + pageCount + "\nPRICE: $" + price + "\nRATING: " + averageRating + "\nSALES: " + totalSales + "\nCONTENT: " + content;
    }

    public void generateISBN() {
        this.isbn = "978-" + (System.currentTimeMillis() % 10000) + "-" + String.format("%03d", (int)(Math.random() * 1000));
    }

    public void calculatePageCount() {
        this.pageCount = content != null ? Math.max(1, content.length() / 250) : 1;
    }

    public boolean validateISBN(String isbn) {
        if (isbn == null || isbn.length() < 10) return false;
        String cleanISBN = isbn.replaceAll("[^0-9X]", "");
        return cleanISBN.length() == 10 || cleanISBN.length() == 13;
    }

    public boolean validatePrice(double price) {
        return price >= 0.0 && price <= 999.99;
    }

    public void addReview(String review, double rating) {
        if (rating >= 1.0 && rating <= 5.0) {
            reviews.add(review);
            averageRating = reviews.size() == 1 ? rating : (averageRating * (reviews.size() - 1) + rating) / reviews.size();
            System.out.println("Review added successfully");
        } else {
            System.out.println("Invalid rating. Must be between 1.0 and 5.0");
        }
    }

    public void recordSale() {
        totalSales++;
        System.out.println("SALES LOG: Book sold - " + title + " (ISBN: " + isbn + ") on " + new java.util.Date());
        System.out.println("INVENTORY: Updating stock for book: " + isbn);
    }

    public double calculateRoyalty(double salePrice) {
        double rate = totalSales > 1000 ? 0.15 : (totalSales > 500 ? 0.12 : 0.10);
        return salePrice * rate;
    }

    public String generateSalesReport() {
        return "=== SALES REPORT ===\nTitle: " + title + "\nAuthor: " + author + "\nISBN: " + isbn + "\nTotal Sales: " + totalSales + "\nAverage Rating: " + String.format("%.2f", averageRating) + "\nNumber of Reviews: " + reviews.size() + "\nCurrent Price: $" + price + "\nEstimated Revenue: $" + String.format("%.2f", totalSales * price);
    }

    public void printFormattedBook() {
        System.out.println("====================\nBOOK INFORMATION\n====================\nTitle: " + title + "\nAuthor: " + author + "\nISBN: " + isbn + "\nGenre: " + genre + "\nPages: " + pageCount + "\nPrice: $" + price + "\nPublisher: " + publisher + "\nRating: " + averageRating + "/5.0\nSales: " + totalSales + "\n====================\nCONTENT:\n" + content + "\n====================");
    }

    public void exportToJSON() {
        String json = "{\n  \"title\": \"" + title + "\",\n  \"author\": \"" + author + "\",\n  \"isbn\": \"" + isbn + "\",\n  \"genre\": \"" + genre + "\",\n  \"pages\": " + pageCount + ",\n  \"price\": " + price + ",\n  \"rating\": " + averageRating + ",\n  \"sales\": " + totalSales + "\n}";
        fileStorage.saveToFile(isbn + ".json", json);
    }

