interface GameCharacter {
    void meleeAttack();
    void castSpell();
    void fly();
    void defend();
}

class Mage implements GameCharacter {
    private String name;
    private int mana;

    public Mage(String name) {
        this.name = name;
        this.mana = 100;
    }

    @Override
    public void meleeAttack() {
        throw new UnsupportedOperationException("Mage cannot perform melee attacks!");
    }

    @Override
    public void castSpell() {
        if (mana >= 20) {
            System.out.println(name + " casts a fireball spell!");
            mana -= 20;
        } else {
            System.out.println(name + " is out of mana!");
        }
    }

    @Override
    public void fly() {
        System.out.println(name + " soars through the air with magic!");
    }

    @Override
    public void defend() {
        System.out.println(name + " creates a magical shield!");
    }
}

class Fighter implements GameCharacter {
    private String name;
    private int stamina;

    public Fighter(String name) {
        this.name = name;
        this.stamina = 100;
    }

    @Override
    public void meleeAttack() {
        if (stamina >= 10) {
            System.out.println(name + " swings sword with great force!");
            stamina -= 10;
        } else {
            System.out.println(name + " is too tired to attack!");
        }
    }

    @Override
    public void castSpell() {
        throw new UnsupportedOperationException("Fighter cannot cast spells!");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Fighter cannot fly!");
    }

    @Override
    public void defend() {
        System.out.println(name + " raises shield to block incoming attacks!");
    }
}

