code_examples.input:
interface MusicPlayer {
    void play();
    void pause();
    void stop();
    void skipTrack();
    void eject();
}

class CDPlayer implements MusicPlayer {
    private boolean isPlaying = false;
    private boolean isPaused = false;
    private String currentTrack = "Track 1";

    @Override
    public void play() {
        isPlaying = true;
        isPaused = false;
        System.out.println("CD Player: Playing " + currentTrack);
    }

    @Override
    public void pause() {
        if (isPlaying) {
            isPaused = true;
            isPlaying = false;
            System.out.println("CD Player: Paused");
        }
    }

    @Override
    public void stop() {
        isPlaying = false;
        isPaused = false;
        System.out.println("CD Player: Stopped");
    }

    @Override
    public void skipTrack() {
        System.out.println("CD Player: Skipping to next track");
        currentTrack = "Track " + (Integer.parseInt(currentTrack.split(" ")[1]) + 1);
    }

    @Override
    public void eject() {
        stop();
        System.out.println("CD Player: CD ejected");
    }
}

class VinylPlayer implements MusicPlayer {
    private boolean isPlaying = false;
    private boolean isPaused = false;
    private String currentSong = "Side A";

    @Override
    public void play() {
        isPlaying = true;
        isPaused = false;
        System.out.println("Vinyl Player: Playing " + currentSong);
    }

    @Override
    public void pause() {
        if (isPlaying) {
            isPaused = true;
            isPlaying = false;
            System.out.println("Vinyl Player: Paused (needle lifted)");
        }
    }

    @Override
    public void stop() {
        isPlaying = false;
        isPaused = false;
        System.out.println("Vinyl Player: Stopped");
    }

    @Override
    public void skipTrack() {
        throw new UnsupportedOperationException("Vinyl players cannot skip tracks - you must manually move the needle!");
    }

    @Override
    public void eject() {
        throw new UnsupportedOperationException("Vinyl players don't eject - you must manually remove the record!");
    }
}

