        System.out.println("Flight Plan: " + plan.getDistance() + "km at " + plan.getAltitude() + "m altitude");

        List<Future<FlightResult>> futures = new ArrayList<>();

        for (FlightCapable flyer : flyers) {
            Future<FlightResult> future = flightExecutor.executeFlight(flyer, plan);
            futures.add(future);
        }

        resultProcessor.processResults(futures, flyers);
    }

    public void performFlightCapabilityAnalysis() {
        flightAnalyzer.analyzeCapabilities(flyers);
    }
