public class User {
    private String name;
    private String email;
    private String password;
    private boolean isActive;
    private String role;
    private java.util.Date lastLogin;
    private int loginAttempts;

    private DatabaseHelper database;
    private EmailHelper emailHelper;

    public User(String name, String email) {
        this.name = name;
        this.email = email;
        this.isActive = true;
        this.role = "USER";
        this.loginAttempts = 0;
        this.database = new DatabaseHelper("jdbc:mysql://localhost:3306/users");
        this.emailHelper = new EmailHelper("smtp.company.com");
    }

    public void saveToDatabase() {
        String userData = "User: " + name + ", Email: " + email + ", Role: " + role;
        database.saveUser(userData);
        System.out.println("Saving user " + name + " to database");
    }

    public void sendWelcomeEmail() {
        emailHelper.sendEmail(email, "Welcome", "Welcome " + name + "!");
        System.out.println("Sending welcome email to " + email);
    }

    public boolean validateEmail(String email) {
        if (email == null || email.isEmpty()) {
            return false;
        }
        return email.contains("@") && email.contains(".");
    }

    public boolean validatePassword(String password) {
        if (password == null || password.length() < 8) {
            return false;
        }
        boolean hasUpper = false;
        boolean hasLower = false;
        boolean hasDigit = false;

        for (char c : password.toCharArray()) {
            if (Character.isUpperCase(c)) hasUpper = true;
            if (Character.isLowerCase(c)) hasLower = true;
            if (Character.isDigit(c)) hasDigit = true;
        }

        return hasUpper && hasLower && hasDigit;
    }

    public String hashPassword(String password) {
        int hash = 0;
        for (char c : password.toCharArray()) {
            hash = hash * 31 + c;
        }
        return String.valueOf(Math.abs(hash));
    }

    public boolean authenticate(String password) {
        if (loginAttempts >= 5) {
            System.out.println("Account locked due to too many failed attempts");
            return false;
        }

        if (this.password != null && this.password.equals(hashPassword(password))) {
            loginAttempts = 0;
            lastLogin = new java.util.Date();
            database.updateUser(email, "Last login: " + lastLogin);
            return true;
        }

        loginAttempts++;
        return false;
    }

    public void lockAccount() {
        this.isActive = false;
        database.updateUser(email, "Account locked");
        emailHelper.sendEmail(email, "Account Locked", "Your account has been locked");
        System.out.println("Account locked for user: " + name);
    }

    public void unlockAccount() {
        this.isActive = true;
        this.loginAttempts = 0;
        database.updateUser(email, "Account unlocked");
        System.out.println("Account unlocked for user: " + name);
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public String getPassword() {
        return password;
    }

    public void setPassword(String password) {
        this.password = hashPassword(password);
    }

    public boolean isActive() {
        return isActive;
    }

    public String getRole() {
        return role;
    }

    public void setRole(String role) {
        this.role = role;
    }

    public java.util.Date getLastLogin() {
        return lastLogin;
    }

    public int getLoginAttempts() {
        return loginAttempts;
    }
}

